
public class SystemEx02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		// 프로그램 종료
		System.out.println("1");
		if (args.length != 1) {
			System.out.println("2");
			//검사 후에 잘못되었을 경우 프로그램 종료
			System.exit(0);
		} else {
			System.out.println("3");
		}
		System.out.println("4");
		
		
		
	}

}
